DECLARE @BatchSize INT = 2000;\nDECLARE @TotalRows INT = 50000;\nDECLARE @CurrentBatch INT = 0;\n\nWHILE @CurrentBatch * @BatchSize < @TotalRows\nBEGIN\n    INSERT INTO YourTable (Column1, Column2, Column3)\n    SELECT TOP (@BatchSize) Column1, Column2, Column3\n    FROM SourceTable\n    WHERE NOT EXISTS (\n        SELECT 1\n        FROM YourTable\n        WHERE YourTable.PrimaryKey = SourceTable.PrimaryKey\n    )\n    ORDER BY SourceTable.PrimaryKey;\n\n    SET @CurrentBatch = @CurrentBatch + 1;\nEND;