-- Check the status of redo logs\nSELECT GROUP#, STATUS, MEMBER FROM V$LOGFILE;\n\n-- Check the current redo log configuration\nSELECT * FROM V$LOG;\n\n-- Check archive log status\nSELECT DEST_NAME, STATUS, ERROR FROM V$ARCHIVE_DEST;\n\n-- Check log buffer statistics\nSELECT * FROM V$SYSSTAT WHERE NAME LIKE 'redo%';\n\n-- Check checkpoint statistics\nSELECT * FROM V$SYSSTAT WHERE NAME LIKE 'background checkpoints%';