-- Step 1: Backup original table\n    SELECT * INTO MyTable_backup FROM MyTable;\n\n    -- Step 2: Create a temporary table with the new schema\n    CREATE TABLE MyTable_temp (\n        Column1 INT,\n        Column2 VARCHAR(100),\n        NewColumn INT\n    );\n\n    -- Step 3: Migrate data\n    INSERT INTO MyTable_temp (Column1, Column2)\n    SELECT Column1, Column2 FROM MyTable;\n\n    -- Step 4: Swap tables\n    EXEC sp_rename 'MyTable', 'MyTable_old';\n    EXEC sp_rename 'MyTable_temp', 'MyTable';