CREATE PROCEDURE UpdateMultipleRecords\n    @Data XML\n    AS\n    BEGIN\n        DECLARE @id INT, @value NVARCHAR(50)\n\n        DECLARE cursor1 CURSOR FOR\n        SELECT \n            Tbl.Col.value('(id)[1]', 'INT'),\n            Tbl.Col.value('(value)[1]', 'NVARCHAR(50)')\n        FROM @Data.nodes('/root/record') Tbl(Col)\n\n        OPEN cursor1\n        FETCH NEXT FROM cursor1 INTO @id, @value\n\n        WHILE @@FETCH_STATUS = 0\n        BEGIN\n            UPDATE your_table SET column2 = @value WHERE id = @id\n            FETCH NEXT FROM cursor1 INTO @id, @value\n        END\n\n        CLOSE cursor1\n        DEALLOCATE cursor1\n    END