SELECT \n    subquery.memId, \n    userDetails.title, \n    userDetails.lname \nFROM \n    (SELECT \n         CONVERT(int, SUBSTRING(accountingTab.id, PATINDEX('%[0-9]%', accountingTab.id), 999)) AS memId, \n         accountingTab.*\n     FROM \n         accountingTab\n    ) AS subquery\nINNER JOIN \n    (SELECT id, title, first, last FROM memDetTab) AS userDetails \nON \n    subquery.memId = userDetails.id;