-- Step 1: Get the total count of results\nSELECT COUNT(*) AS TotalCount\nFROM YourTable\nWHERE YourConditions;\n\n-- Step 2: Paginate results using ROW_NUMBER()\nDECLARE @PageNumber INT = 1;  -- Set the page number\nDECLARE @RowsPerPage INT = 10; -- Set the number of rows per page\n\nWITH PaginatedResults AS (\n    SELECT *,\n           ROW_NUMBER() OVER (ORDER BY YourSortColumn) AS RowNum\n    FROM YourTable\n    WHERE YourConditions\n)\nSELECT *\nFROM PaginatedResults\nWHERE RowNum BETWEEN (@PageNumber - 1) * @RowsPerPage + 1 AND @PageNumber * @RowsPerPage;