-- Step 1: Get the total count of results\nSELECT COUNT(*) AS TotalCount\nFROM YourTable\nWHERE YourConditions;\n\n-- Step 2: Paginate results using OFFSET-FETCH\nDECLARE @PageNumber INT = 1;  -- Set the page number\nDECLARE @RowsPerPage INT = 10; -- Set the number of rows per page\n\nSELECT *\nFROM YourTable\nWHERE YourConditions\nORDER BY YourSortColumn\nOFFSET (@PageNumber - 1) * @RowsPerPage ROWS\nFETCH NEXT @RowsPerPage ROWS ONLY;