USE msdb;\nGO\n\n-- Query to get the status of a specific job\nDECLARE @job_name NVARCHAR(128) = 'YourJobName';\n\nSELECT \n    job.name AS JobName,\n    jobh.run_date AS RunDate,\n    jobh.run_time AS RunTime,\n    jobh.run_status AS RunStatus,\n    CASE jobh.run_status\n        WHEN 0 THEN 'Failed'\n        WHEN 1 THEN 'Succeeded'\n        WHEN 2 THEN 'Retry'\n        WHEN 3 THEN 'Canceled'\n        WHEN 4 THEN 'In-progress'\n    END AS StatusDescription,\n    jobh.message AS Message\nFROM \n    sysjobs AS job\nJOIN \n    sysjobhistory AS jobh ON job.job_id = jobh.job_id\nWHERE \n    job.name = @job_name\nORDER BY \n    jobh.run_date DESC, jobh.run_time DESC;