WITH BinaryConversion AS (\n    SELECT \n        CAST(5 AS INT) AS Number, \n        CAST('' AS VARCHAR(MAX)) AS BinaryString\n    UNION ALL\n    SELECT \n        Number / 2,\n        CAST(CAST(Number % 2 AS VARCHAR) + BinaryString AS VARCHAR(MAX))\n    FROM \n        BinaryConversion\n    WHERE \n        Number > 0\n)\nSELECT TOP 1 BinaryString\nFROM BinaryConversion\nORDER BY Number DESC;