CREATE OR REPLACE PROCEDURE my_procedure (\n    param1 IN VARCHAR2,\n    param2 IN NUMBER,\n    param3 IN VARCHAR2,\n    -- more parameters\n    param20 IN VARCHAR2\n) IS\nBEGIN\n    -- Check for NULL values in required parameters\n    IF param1 IS NULL OR param2 IS NULL OR param3 IS NULL THEN\n        RAISE_APPLICATION_ERROR(-20001, 'Required parameter(s) cannot be NULL');\n    END IF;\n    \n    -- Your procedure logic here\n    \nEND my_procedure;