CREATE OR REPLACE FUNCTION encrypt_clob(p_clob IN CLOB) RETURN BLOB IS\n    v_input_blob BLOB;\n    v_encrypted_blob BLOB;\n    key RAW(16) := UTL_RAW.cast_to_raw('YourSecretKey123'); -- Example key, use a secure key\n    iv RAW(16) := UTL_RAW.cast_to_raw('YourInitVector123'); -- Example IV\n    \nBEGIN\n    -- Convert CLOB to BLOB\n    DBMS_LOB.createtemporary(v_input_blob, TRUE);\n    DBMS_LOB.converttoblob(v_input_blob, p_clob, DBMS_LOB.lobmaxsize, 1, 1, 0, 1);\n    \n    -- Encrypt the BLOB\n    v_encrypted_blob := DBMS_CRYPTO.encrypt(\n        src => v_input_blob,\n        typ => DBMS_CRYPTO.DES_CBC_PKCS5,\n        key => key,\n        iv => iv\n    );\n\n    RETURN v_encrypted_blob;\nEND;\n/