CREATE TABLE people (\n     id       INTEGER PRIMARY KEY,\n     name     VARCHAR(100)\n   );\n\n   CREATE TABLE archived_people (\n     id       INTEGER PRIMARY KEY,\n     name     VARCHAR(100)\n   );\n\n   -- Query to get active people\n   SELECT * FROM people;\n\n   -- Query to get both active and inactive people\n   SELECT * FROM people\n   UNION ALL\n   SELECT * FROM archived_people;