-- First, create a stored procedure to set the isolation level\nCREATE PROCEDURE SetReadUncommitted\nAS\nBEGIN\n    SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED;\nEND\nGO\n\n-- Then, create a logon trigger to execute the stored procedure for the specific user\nCREATE TRIGGER ForceNoLockHint\nON ALL SERVER\nFOR LOGON\nAS\nBEGIN\n    DECLARE @LoginName NVARCHAR(100);\n    SET @LoginName = ORIGINAL_LOGIN();\n\n    -- Replace 'YourSupportUser' with the actual login name\n    IF @LoginName = 'YourSupportUser'\n    BEGIN\n        EXEC SetReadUncommitted;\n    END\nEND\nGO