WITH RECURSIVE tree AS (\n    -- Anchor member: Start with the given parentid\n    SELECT \n        id, \n        name, \n        parentid, \n        value\n    FROM \n        example\n    WHERE \n        parentid = <your_parent_id>  -- Replace with your actual parent id\n\n    UNION ALL\n\n    -- Recursive member: Join with the example table on parentid\n    SELECT \n        e.id, \n        e.name, \n        e.parentid, \n        e.value\n    FROM \n        example e\n    INNER JOIN \n        tree t ON e.parentid = t.id\n)\nSELECT \n    SUM(value) AS total_value\nFROM \n    tree;