CREATE PROCEDURE UpdatePrices\n    @param1 DECIMAL(10, 2),\n    @param2 DECIMAL(10, 2),\n    @param3 DECIMAL(10, 2),\n    @param4 DECIMAL(10, 2),\n    @param5 INT\nAS\nBEGIN\n    UPDATE sometable\n    SET \n        price1 = COALESCE(@param1, price1),\n        price2 = COALESCE(@param2, price2),\n        price3 = COALESCE(@param3, price3),\n        price4 = COALESCE(@param4, price4)\n    WHERE\n        ID = @param5;\nEND