-- Create a trace\nDECLARE @TraceID INT;\nEXEC sp_trace_create @TraceID OUTPUT, 0, N'C:\TraceFiles\MyTrace';\n-- Add events to the trace (example: SQL:BatchCompleted)\nEXEC sp_trace_setevent @TraceID, 12, 1, 1;\nEXEC sp_trace_setevent @TraceID, 12, 2, 1;\n-- Start the trace\nEXEC sp_trace_setstatus @TraceID, 1;\n-- Stop and close the trace when done\n-- EXEC sp_trace_setstatus @TraceID, 0;\n-- EXEC sp_trace_setstatus @TraceID, 2;