CREATE PROCEDURE processOrder (@orderId as numeric) AS\nBEGIN\n    -- Create temporary tables to store the result sets\n    CREATE TABLE #OrderDetails (\n        order_address NVARCHAR(255),\n        order_number NVARCHAR(255)\n    );\n\n    CREATE TABLE #OrderItems (\n        item NVARCHAR(255),\n        number_of_items INT,\n        cost DECIMAL(10, 2)\n    );\n\n    -- Insert the result sets into the temporary tables\n    INSERT INTO #OrderDetails\n    EXEC getOrder @orderId;\n\n    -- The second result set will automatically be captured\n    INSERT INTO #OrderItems\n    EXEC getOrder @orderId;\n\n    -- Now you can work with the temporary tables\n    -- Example: Iterate through the first result set\n    SELECT * FROM #OrderDetails;\n\n    -- Example: Iterate through the second result set\n    SELECT * FROM #OrderItems;\n\n    -- Clean up temporary tables\n    DROP TABLE #OrderDetails;\n    DROP TABLE #OrderItems;\nEND