-- Compare table structures between two schemas\nSELECT \n    t1.table_name, t1.column_name, t1.data_type\nFROM \n    information_schema.columns t1\nLEFT JOIN \n    other_schema.information_schema.columns t2\nON \n    t1.table_name = t2.table_name \n    AND t1.column_name = t2.column_name\nWHERE \n    t2.column_name IS NULL\nUNION ALL\nSELECT \n    t2.table_name, t2.column_name, t2.data_type\nFROM \n    information_schema.columns t1\nRIGHT JOIN \n    other_schema.information_schema.columns t2\nON \n    t1.table_name = t2.table_name \n    AND t1.column_name = t2.column_name\nWHERE \n    t1.column_name IS NULL;