-- Complex query without temp table\nSELECT a.*, b.*, c.*\nFROM tableA a\nJOIN tableB b ON a.id = b.id\nJOIN tableC c ON b.id = c.id\nWHERE a.some_column = 'some_value';\n\n-- Using temp table to break down the problem\n-- Step 1: Create temp table and insert intermediate results\nCREATE TEMPORARY TABLE temp_table AS\nSELECT a.*, b.*\nFROM tableA a\nJOIN tableB b ON a.id = b.id\nWHERE a.some_column = 'some_value';\n\n-- Step 2: Use temp table for final join\nSELECT temp.*, c.*\nFROM temp_table temp\nJOIN tableC c ON temp.id = c.id;