-- Create an in-memory optimized table\nCREATE TABLE dbo.InMemoryTable (\n    ID INT NOT NULL PRIMARY KEY NONCLUSTERED,\n    Name NVARCHAR(50),\n    Value DECIMAL(10, 2)\n) WITH (MEMORY_OPTIMIZED=ON, DURABILITY=SCHEMA_ONLY);\n\n-- Insert data into the in-memory table\nINSERT INTO dbo.InMemoryTable (ID, Name, Value)\nVALUES (1, 'SampleName', 123.45);\n\n-- Use the in-memory table in a JOIN with a permanent table\nSELECT p.ID, p.PermanentColumn, i.Name, i.Value\nFROM PermanentTable p\nJOIN dbo.InMemoryTable i ON p.ID = i.ID;