VAR result_set REFCURSOR\n\nDECLARE\n   my_tbl_1 NUMBER := 1;\n   my_tbl_2 NUMBER := 0;\n   my_tbl_3 NUMBER := 0;\n   query_str VARCHAR2(1000);\nBEGIN\n   IF (my_tbl_1 > 0) THEN\n      query_str := 'SELECT * FROM my_tbl_1 ORDER BY my_tbl_1_id DESC';\n   ELSIF (my_tbl_2 > 0) THEN\n      query_str := 'SELECT * FROM my_tbl_2 ORDER BY my_tbl_2_id DESC';\n   ELSIF (my_tbl_3 > 0) THEN\n      query_str := 'SELECT * FROM my_tbl_3 ORDER BY my_tbl_3_id DESC';\n   END IF;\n\n   IF query_str IS NOT NULL THEN\n      OPEN :result_set FOR query_str;\n   END IF;\nEND;\n/\n\nPRINT result_set