-- Create a local temporary table\nCREATE TABLE #TempTable (\n    ID INT,\n    Name NVARCHAR(50),\n    Value DECIMAL(10, 2)\n);\n\n-- Insert data into the temporary table\nINSERT INTO #TempTable (ID, Name, Value)\nVALUES (1, 'SampleName', 123.45);\n\n-- Use the temporary table in a JOIN with a permanent table\nSELECT p.ID, p.PermanentColumn, t.Name, t.Value\nFROM PermanentTable p\nJOIN #TempTable t ON p.ID = t.ID;\n\n-- Drop the temporary table when done\nDROP TABLE #TempTable;