-- Creating a view\nCREATE VIEW vw_EmployeeDetails AS\nSELECT e.EmployeeID, e.FirstName, e.LastName, d.DepartmentName\nFROM Employees e\nJOIN Departments d ON e.DepartmentID = d.DepartmentID;\n\n-- Using a stored procedure for a complex operation\nCREATE PROCEDURE sp_GetEmployeeReport\nAS\nBEGIN\n    SELECT e.EmployeeID, e.FirstName, e.LastName, d.DepartmentName, COUNT(p.ProjectID) AS ProjectCount\n    FROM Employees e\n    JOIN Departments d ON e.DepartmentID = d.DepartmentID\n    LEFT JOIN Projects p ON e.EmployeeID = p.EmployeeID\n    GROUP BY e.EmployeeID, e.FirstName, e.LastName, d.DepartmentName;\nEND;