DECLARE\n  tablespace_name VARCHAR2(30) := 'USERS';\n  avg_row_size NUMBER := 62; -- Average row size in bytes\n  row_count NUMBER := 1000000; -- Number of rows\n  used_bytes NUMBER;\n  alloc_bytes NUMBER;\nBEGIN\n  DBMS_SPACE.CREATE_TABLE_COST(\n    tablespace_name => tablespace_name,\n    avg_row_size => avg_row_size,\n    row_count => row_count,\n    pct_free => 10,\n    used_bytes => used_bytes,\n    alloc_bytes => alloc_bytes\n  );\n  DBMS_OUTPUT.PUT_LINE('Estimated Used Bytes: ' || used_bytes);\n  DBMS_OUTPUT.PUT_LINE('Estimated Allocated Bytes: ' || alloc_bytes);\nEND;\n/