-- Insert current state into EmployeeVersions\nINSERT INTO EmployeeVersions (EmployeeId, FirstName, LastName, DepartmentId, DateModified)\nSELECT EmployeeId, FirstName, LastName, DepartmentId, NOW()\nFROM Employees\nWHERE EmployeeId = 1;\n\n-- Update Employees table with new data and update CurrentVersionId\nUPDATE Employees\nSET FirstName = 'NewFirstName',\n    LastName = 'NewLastName',\n    DepartmentId = 2,\n    CurrentVersionId = (SELECT MAX(VersionId) FROM EmployeeVersions WHERE EmployeeId = 1)\nWHERE EmployeeId = 1;