-- Creating a table with a GUID primary key\nCREATE TABLE PurchaseOrder (\n    PurchaseOrderID UNIQUEIDENTIFIER DEFAULT NEWID() PRIMARY KEY,\n    OrderDate DATE,\n    CustomerID UNIQUEIDENTIFIER,\n    Amount DECIMAL(10, 2)\n);\n\n-- Using sequential GUID for SQL Server\nCREATE TABLE PurchaseOrder (\n    PurchaseOrderID UNIQUEIDENTIFIER DEFAULT NEWSEQUENTIALID() PRIMARY KEY,\n    OrderDate DATE,\n    CustomerID UNIQUEIDENTIFIER,\n    Amount DECIMAL(10, 2)\n);