SELECT\n  SUM(NumberOfBrides) AS [Wedding Count]\n  , DATEPART(wk, WeddingDate) AS [Week Number]\n  , DATEPART(year, WeddingDate) AS [Year]\n  , DATEADD(day, 1 - DATEPART(WEEKDAY, DATEADD(day, 1 - DATEPART(WEEKDAY, WeddingDate), WeddingDate)), DATEADD(day, 1 - DATEPART(WEEKDAY, WeddingDate), WeddingDate)) AS WeekStart\n  , DATEADD(day, 7 - DATEPART(WEEKDAY, DATEADD(day, 1 - DATEPART(WEEKDAY, WeddingDate), WeddingDate)), DATEADD(day, 1 - DATEPART(WEEKDAY, WeddingDate), WeddingDate)) AS WeekEnd\nFROM MemberWeddingDates\nGROUP BY DATEPART(year, WeddingDate), DATEPART(wk, WeddingDate)\nORDER BY SUM(NumberOfBrides) DESC