DECLARE @xml XML = '<root><element>Test</element><element>example</element></root>'\nDECLARE @searchString NVARCHAR(50) = 'test'\n\nSELECT @xml.query('\n    /root/element[contains(translate(., "ABCDEFGHIJKLMNOPQRSTUVWXYZ", "abcdefghijklmnopqrstuvwxyz"), \n                           translate(sql:variable("@searchString"), \n                                     "ABCDEFGHIJKLMNOPQRSTUVWXYZ", \n                                     "abcdefghijklmnopqrstuvwxyz"))]\n') AS MatchingElements