-- Recursive factorial function (not optimized)\nCREATE OR REPLACE FUNCTION factorial_recursive(n NUMBER) RETURN NUMBER IS\nBEGIN\n    IF n = 0 THEN\n        RETURN 1;\n    ELSE\n        RETURN n * factorial_recursive(n - 1);\n    END IF;\nEND;\n/\n\n-- Iterative factorial function (optimized)\nCREATE OR REPLACE FUNCTION factorial_iterative(n NUMBER) RETURN NUMBER IS\n    result NUMBER := 1;\nBEGIN\n    FOR i IN 1..n LOOP\n        result := result * i;\n    END LOOP;\n    RETURN result;\nEND;\n/