CREATE TABLE submissions (\n    id INT AUTO_INCREMENT PRIMARY KEY,\n    title VARCHAR(255),\n    content TEXT,\n    user_id INT,\n    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP\n);\n\nCREATE TABLE votes (\n    id INT AUTO_INCREMENT PRIMARY KEY,\n    submission_id INT,\n    user_id INT,\n    vote INT, -- 1 for upvote, -1 for downvote\n    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP\n);