-- Add a new column to store move\nALTER TABLE games ADD COLUMN move CHAR(1);\n\n-- Update the table to store move value\nUPDATE games SET move = SUBSTRING(game, {$len}, 1);\n\n-- Create an index on the new move column\nCREATE INDEX idx_move ON games (move);\n\n-- Optimized Query using the new move column\nSELECT move,\n       COUNT(*) AS games,\n       SUM(win) AS wins,\n       SUM(loss) AS losses\nFROM games\nWHERE game > {$something} AND game < {$something_else}\nGROUP BY move;