WITH SalesData AS (\n    SELECT\n        Category,\n        Year,\n        Month,\n        Revenue\n    FROM\n        PVMonthlyStatusReport\n    WHERE\n        Category = 'Bikes' AND\n        (Year = 2007 OR Year = 2008)\n),\nAllMonths AS (\n    SELECT DISTINCT\n        Category,\n        Month\n    FROM\n        SalesData\n    WHERE\n        Year = 2007 OR Year = 2008\n)\nSELECT\n    AM.Category,\n    AM.Month,\n    ISNULL(SD2008.Revenue, 0) AS [Rev. This Year],\n    ISNULL(SD2007.Revenue, 0) AS [Rev. Last Year]\nFROM\n    AllMonths AM\n    LEFT JOIN SalesData SD2008 ON AM.Category = SD2008.Category AND AM.Month = SD2008.Month AND SD2008.Year = 2008\n    LEFT JOIN SalesData SD2007 ON AM.Category = SD2007.Category AND AM.Month = SD2007.Month AND SD2007.Year = 2007\nORDER BY\n    AM.Month;