SELECT *\nFROM your_table\nWHERE \n(\n    -- If the day is Friday, add 3 days to get the next Monday\n    DATEPART(dw, your_date_column) = 6 AND DATEADD(day, 3, your_date_column) = CAST(GETDATE() AS date)\n) OR \n(\n    -- If the day is Saturday, add 2 days to get the next Monday\n    DATEPART(dw, your_date_column) = 7 AND DATEADD(day, 2, your_date_column) = CAST(GETDATE() AS date)\n) OR \n(\n    -- For other weekdays, just add 1 day\n    DATEPART(dw, your_date_column) NOT IN (6, 7) AND DATEADD(day, 1, your_date_column) = CAST(GETDATE() AS date)\n)