SELECT *\nFROM your_table\nWHERE \n(\n    -- If the day is Friday, add 3 days to get the next Monday\n    DAYOFWEEK(your_date_column) = 6 AND DATE_ADD(your_date_column, INTERVAL 3 DAY) = CURDATE()\n) OR \n(\n    -- If the day is Saturday, add 2 days to get the next Monday\n    DAYOFWEEK(your_date_column) = 7 AND DATE_ADD(your_date_column, INTERVAL 2 DAY) = CURDATE()\n) OR \n(\n    -- For other weekdays, just add 1 day\n    DAYOFWEEK(your_date_column) NOT IN (6, 7) AND DATE_ADD(your_date_column, INTERVAL 1 DAY) = CURDATE()\n)