-- Variables for paging\nDECLARE @PageNumber INT\nDECLARE @PageSize INT\n\n-- Set the desired page number and page size\nSET @PageNumber = 1\nSET @PageSize = 10\n\n-- Calculate the starting row number and ending row number\nDECLARE @StartRow INT\nDECLARE @EndRow INT\n\nSET @StartRow = (@PageNumber - 1) * @PageSize + 1\nSET @EndRow = @PageNumber * @PageSize\n\n-- Create a temporary table to store row numbers\nCREATE TABLE #TempTable\n(\n    RowNumber INT,\n    Operator INT,\n    CustomerIdentifier INT,\n    DateDisconnected DATETIME\n)\n\n-- Insert data with row numbers into the temporary table\nINSERT INTO #TempTable (RowNumber, Operator, CustomerIdentifier, DateDisconnected)\nSELECT\n    (SELECT COUNT(*)\n     FROM YourTable AS T2\n     WHERE T2.Operator <= T1.Operator\n       AND (T2.Operator < T1.Operator\n            OR (T2.CustomerIdentifier <= T1.CustomerIdentifier\n                AND (T2.CustomerIdentifier < T1.CustomerIdentifier\n                     OR T2.DateDisconnected <= T1.DateDisconnected)))) AS RowNumber,\n    T1.Operator,\n    T1.CustomerIdentifier,\n    T1.DateDisconnected\nFROM YourTable AS T1\nORDER BY Operator, CustomerIdentifier, DateDisconnected\n\n-- Select the desired page of results\nSELECT Operator, CustomerIdentifier, DateDisconnected\nFROM #TempTable\nWHERE RowNumber BETWEEN @StartRow AND @EndRow\nORDER BY RowNumber\n\n-- Drop the temporary table\nDROP TABLE #TempTable