-- Create schemas\nCREATE SCHEMA internal;\nCREATE SCHEMA public;\n\n-- Create an internal stored procedure\nCREATE PROCEDURE internal.usp_InternalProcedure\nAS\nBEGIN\n    -- Procedure logic here\nEND;\n\n-- Create a public stored procedure that calls the internal one\nCREATE PROCEDURE public.usp_PublicProcedure\nAS\nBEGIN\n    -- Call the internal procedure\n    EXEC internal.usp_InternalProcedure;\nEND;\n\n-- Grant execute permissions on the public schema to a role or user\nGRANT EXECUTE ON SCHEMA::public TO [RoleOrUser];\n\n-- Deny execute permissions on the internal schema to all except specific roles or users\nDENY EXECUTE ON SCHEMA::internal TO public;\nGRANT EXECUTE ON SCHEMA::internal TO [SpecificRoleOrUser];