CREATE PROCEDURE GetNextID\n    @TableName NVARCHAR(255),\n    @NextID INT OUTPUT\nAS\nBEGIN\n    BEGIN TRANSACTION\n    DECLARE @LastID INT;\n    \n    -- Get the last used ID\n    SELECT @LastID = LastUsedID\n    FROM IDTracker\n    WHERE TableName = @TableName;\n    \n    -- Increment the ID\n    SET @NextID = @LastID + 1;\n\n    -- Update the tracker with the new ID\n    UPDATE IDTracker\n    SET LastUsedID = @NextID\n    WHERE TableName = @TableName;\n    \n    COMMIT TRANSACTION;\nEND;