CREATE TABLE Comments (\n    CommentID INT PRIMARY KEY AUTO_INCREMENT,\n    ParentObjectID INT NOT NULL,\n    ParentObjectType VARCHAR(50) NOT NULL,\n    UserID INT NOT NULL,\n    CommentText TEXT,\n    CreatedAt TIMESTAMP DEFAULT CURRENT_TIMESTAMP,\n    FOREIGN KEY (UserID) REFERENCES Users(UserID)\n);\n\n-- Example of querying comments for a specific object type\n-- Assuming you want to get all comments for an object of type 'ObjectA' with ID 1\nSELECT * FROM Comments\nWHERE ParentObjectType = 'ObjectA' AND ParentObjectID = 1;