-- Script to generate statistics script\nSELECT \n    'CREATE STATISTICS ' + QUOTENAME(s.name) + ' ON ' + \n    QUOTENAME(OBJECT_SCHEMA_NAME(s.object_id)) + '.' + \n    QUOTENAME(OBJECT_NAME(s.object_id)) + ' (' + \n    STRING_AGG(QUOTENAME(c.name), ', ') + ')' \nFROM \n    sys.stats AS s\nJOIN \n    sys.stats_columns AS sc ON s.object_id = sc.object_id AND s.stats_id = sc.stats_id\nJOIN \n    sys.columns AS c ON sc.object_id = c.object_id AND sc.column_id = c.column_id\nWHERE \n    OBJECT_NAME(s.object_id) = 'YourTableName'\nGROUP BY \n    s.name, s.object_id;