-- Create a classification function\nCREATE FUNCTION dbo.ClassifyQuery() RETURNS SYSNAME\nWITH SCHEMABINDING\nAS\nBEGIN\n    DECLARE @WorkloadGroup SYSNAME;\n\n    -- Example logic to classify based on application name\n    IF (APP_NAME() = 'YourLowPriorityApp')\n        SET @WorkloadGroup = 'LowPriorityGroup';\n    ELSE IF (APP_NAME() = 'YourHighPriorityApp')\n        SET @WorkloadGroup = 'HighPriorityGroup';\n    ELSE\n        SET @WorkloadGroup = 'default';\n\n    RETURN @WorkloadGroup;\nEND;