-- Function to remove non-alphanumeric characters\nCREATE FUNCTION dbo.RemoveNonAlphaNumeric (@str VARCHAR(MAX))\nRETURNS VARCHAR(MAX)\nAS\nBEGIN\n    DECLARE @cleanedStr VARCHAR(MAX) = ''\n    DECLARE @i INT = 1\n    DECLARE @currentChar CHAR(1)\n\n    WHILE @i <= LEN(@str)\n    BEGIN\n        SET @currentChar = SUBSTRING(@str, @i, 1)\n        IF PATINDEX('%[0-9a-zA-Z]%', @currentChar) > 0\n        BEGIN\n            SET @cleanedStr = @cleanedStr + @currentChar\n        END\n        SET @i = @i + 1\n    END\n\n    RETURN @cleanedStr\nEND\nGO\n\n-- Update the column using the function\nUPDATE myTable\nSET myColumn = dbo.RemoveNonAlphaNumeric(myColumn)\nWHERE myColumn IS NOT NULL\nGO