private function initApp():void {
// Initialize shared resources
// e.g., database connection, data manager, etc.
}

private function loadModule(moduleURL:String):void {
var moduleLoader:ModuleLoader = new ModuleLoader();
moduleLoader.url = moduleURL;
moduleLoader.addEventListener(ModuleEvent.READY, moduleReadyHandler);
moduleLoader.loadModule();
}

private function moduleReadyHandler(event:ModuleEvent):void {
// Module is ready, add it to the display
var moduleLoader:ModuleLoader = event.target as ModuleLoader;
this.addElement(moduleLoader);
}
