#!/bin/bash\n\n# Check if exactly one parameter is provided\nif [ $# -ne 1 ]; then\n  echo "Usage: $0 <id-number>"\n  exit 1\nfi\n\n# Check if the parameter is numerical\nif ! [[ $1 =~ ^[0-9]+$ ]]; then\n  echo "Error: id-number must be numerical"\n  exit 1\nfi\n\n# Check if the folder exists\nif [ ! -d "~/myfolder1/$1/anotherfolder" ]; then\n  echo "Error: Folder does not exist"\n  exit 1\nfi\n\n# If all checks pass, proceed with the deletion\nrm -rf ~/myfolder1/$1/anotherfolder\nrm -rf ~/myfolder2/$1/yetanotherfolder\nrm -rf ~/myfolder3/$1/thisisafolder