#!/bin/bash\n\n# Weekly full backup\nrsync -avz --cvs-exclude /path/to/cvs/repo/ /backup/weekly/\n\n# Nightly incremental backup\nrsync -avz --cvs-exclude --delete /path/to/cvs/repo/ /backup/nightly/\n\n# Compress and split backups\ngzip -c /backup/weekly/ > /backup/weekly.tar.gz\nsplit -b 1G /backup/weekly.tar.gz /backup/weekly-split-\n\n# Verify backups\ncvs -z3 -d /backup/weekly/ co -p -r HEAD myproject