#!/bin/bash\n\n# Set source and destination directories\nSRC_DIR=/home/user\nDST_DIR=/backup/user\n\n# Set the date for the current snapshot\nDATE=$(date +"%Y%m%d_%H%M%S")\n\n# Create a new snapshot directory\nmkdir -p "$DST_DIR/$DATE"\n\n# Run rsync with --link-dest and --incremental options\nrsync -avx --link-dest="$DST_DIR/latest" --incremental "$SRC_DIR" "$DST_DIR/$DATE"\n\n# Update the "latest" link to point to the new snapshot\nln -sf "$DST_DIR/$DATE" "$DST_DIR/latest"