#!/bin/bash\n\n# Move files from Downloads to designated folders\nhazelMove() {\n  local src=$1\n  local dst=$2\n  local file\n\n  for file in "$src"/*; do\n    if [ -f "$file" ]; then\n      mv "$file" "$dst"\n    fi\n  done\n}\n\n# Set source and destination folders\ndownloadsFolder="$HOME/Downloads"\nprojectsFolder="$HOME/Projects"\n\n# Move files from Downloads to Projects folder\nhazelMove "$downloadsFolder" "$projectsFolder"