# Add SharePoint PowerShell module\nAdd-PSSnapin Microsoft.SharePoint.PowerShell\n\n# Set site and list URLs\n$siteUrl = "https://example.sharepoint.com"\n$listUrl = "https://example.sharepoint.com/lists/Tasks"\n\n# Get site and list\n$site = Get-SPSite $siteUrl\n$list = $site.RootWeb.GetList($listUrl)\n\n# Get tasks and set permissions\n$tasks = $list.Items\nforeach ($task in $tasks) {\n   $assignedTo = $task["Assigned To"]\n   $user = Get-SPUser -Identity $assignedTo -Web $site.RootWeb\n   $task.BreakRoleInheritance($false)\n   $task.RoleAssignments.Add($user)\n   $task.RoleAssignments.Add($list.RoleAssignments.GetAssignmentByUser($user))\n   $task.Update()\n}