# Copy files\nCopy-Item -Path "C:\OldServer\WebApp" -Destination "C:\NewServer\WebApp" -Recurse\n\n# Update web.config\n$configFile = "C:\NewServer\WebApp\web.config"\n$xml = [xml](Get-Content $configFile)\n$xml.configuration.ConnectionStrings.add | Where-Object {$_.name -eq "MyDB"} | ForEach-Object {$_.connectionString = "Server=newServer;Database=myDB;User Id=myUser;Password=myPassword;"}\n$xml.Save($configFile)