# Get the selected folders\n$folders = @()\nforeach ($item in (Get-Item -Path $args[0])) {\n    if ($item -is [System.IO.DirectoryInfo]) {\n        $folders += $item.FullName\n    }\n}\n\n# Prompt for backup name and description\n$backupName = Read-Host "Enter backup name"\n$backupDescription = Read-Host "Enter backup description"\n\n# Create the backup directory\n$backupDir = "C:\Backups\$backupName"\nNew-Item -ItemType Directory -Path $backupDir\n\n# Move the selected folders to the backup directory\nforeach ($folder in $folders) {\n    Move-Item -Path $folder -Destination $backupDir\n}\n\n# Create a readme.txt file with backup details\n$readmePath = Join-Path -Path $backupDir -ChildPath "readme.txt"\nSet-Content -Path $readmePath -Value "Backup name: $backupName`nBackup description: $backupDescription`nDate: $(Get-Date)"