ARCH = $(shell uname -m)\n\nifeq ($(ARCH), i386)\n    LIBDIR = ~/lib/i386\n    INCLUDEDIR = ~/include/i386\nendif\n\nifeq ($(ARCH), x86_64)\n    LIBDIR = ~/lib/x86_64\n    INCLUDEDIR = ~/include/x86_64\nendif\n\n# Example for armv7l\nifeq ($(ARCH), armv7l)\n    LIBDIR = ~/lib/armv7l\n    INCLUDEDIR = ~/include/armv7l\nendif\n\nCFLAGS = -I$(INCLUDEDIR)\nLDFLAGS = -L$(LIBDIR)\n\n# Rest of your Makefile